import 'package:flutter/material.dart';class HomePage extends StatelessWidget {  const HomePage({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    Widget buildWideContainers() {      return const SingleChildScrollView(        child: Column(children: [Text("Inicia sesión para continuar")]),      );    }    Widget buildPhoneContainer() {      return const SingleChildScrollView(        child: Column(children: [Text("Inicia sesión para continuar")]),      );    }    return LayoutBuilder(      builder: (BuildContext context, BoxConstraints constraints) {        if (constraints.maxWidth > 600) {          return buildWideContainers();        } else {          return buildPhoneContainer();        }      },    );  }}